import 'package:flutter/material.dart';

import 'descricao.dart';
class SeriesPage extends StatefulWidget {
  const SeriesPage({Key? key, required this.title}) : super(key: key);

  final String title;

  @override
  State<SeriesPage> createState() => _SeriesPageState();
}

class _SeriesPageState extends State<SeriesPage> {
  int _counter = 0;

  void _incluirAfazer() {
    setState(() {
      _counter++;
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(widget.title),
      ),
      body: ListView(
        children: [
          ListTile(
            onTap: (){
              Navigator.push(context,
                  MaterialPageRoute(builder: (context) => const DescricaoPage(title: 'Anime '))
              );
            },
            title: const Text('Breaking Bad'),
            leading: const Image(
                image: NetworkImage('data:image/jpeg;base64,'),
              ),
          ),
        ],
      ),
      floatingActionButtonLocation: FloatingActionButtonLocation.centerDocked,
      floatingActionButton: FloatingActionButton(
        onPressed: _incluirAfazer,
        tooltip: 'Increment',
        child: const Icon(Icons.add),
      ),

    );
  }
}



